BEGIN TRANSACTION;
CREATE TABLE category (id integer primary key not null, name varchar(100), position int, image text, icon text, show_menu char(1), description TEXT, link text);
CREATE TABLE media (id integer primary key not null, name text, type text, code text, url text, author text);
CREATE TABLE block (id int, name text, title text, content text);
CREATE TABLE post (id integer primary key not null, title text, subtitle text, content text, login text, post_date date, tags text, image text, category_id integer references category(id), media_id integer references media(id));
COMMIT;
